// Process and filter tweets
const tweets = $input.first().json.tweets || [];

// Filter out replies and process tweets
const processedTweets = tweets
  .filter(tweet => !tweet.in_reply_to_status_id) // Remove replies
  .map(tweet => ({
    id: tweet.id_str,
    title: tweet.full_text || tweet.text,
    username: tweet.user.screen_name,
    user_name: tweet.user.name,
    user_id: tweet.user.id_str,
    created_at: tweet.tweet_created_at,
    timestamp: new Date().toISOString(),
    likes_count: tweet.favorite_count || 0,
    retweets_count: tweet.retweet_count || 0,
    replies_count: tweet.reply_count || 0,
    views_count: tweet.views_count || 0,
    url: `https://twitter.com/${tweet.user.screen_name}/status/${tweet.id_str}`,
    hashtags: tweet.entities?.hashtags?.map(h => h.text) || [],
    mentions: tweet.entities?.user_mentions?.map(m => m.screen_name) || [],
    media_urls: tweet.entities?.media?.map(m => m.media_url_https) || [],
    is_quote: tweet.is_quote_status || false,
    lang: tweet.lang || 'tr',
    source: 'socialapi.me'
  }));

// Return processed data
return {
  tweets: processedTweets,
  count: processedTweets.length,
  processed_at: new Date().toISOString(),
  user: 'ozan_sihay',
  source: 'n8n_workflow'
};
